property.code:
define('swaggerNotifications', {
  prefix: 'swagger.notifications.',
  keys: [
    'invalid'
  ]
});

require(['jquery', 'swagger-ui', 'yamltojson', 'xwiki-l10n!swaggerNotifications'], function ($, SwaggerUIBundle, yamltojson, l10n) {

  // Fix for Array.prototype.entries since we are using the prototype framework and the method is missing.
  if (!Array.prototype.entries || typeof Array.prototype.entries !== 'function') {
    Array.prototype.entries = function* () {
      for (let i = 0; i < this.length; i++) {
        yield [i, this[i]];
      }
    };
  }


  function shadowRootContainer(swaggerContainer) {
    if (!swaggerContainer[0].shadowRoot) {
      // Create a shadow root to isolate Swagger UI css because it interacts in a bad way with the XWiki css.
      const shadowRoot = swaggerContainer[0].attachShadow({ mode: 'open' });
      // Add the Swagger UI CSS to the shadow DOM.
      const styleLink = document.createElement('link');
      styleLink.rel = "stylesheet";
      //TODO find a better way to get this.
      styleLink.href = "$services.webjars.url('org.webjars:swagger-ui', 'swagger-ui.css')";
      shadowRoot.appendChild(styleLink);
      const shadowContent = document.createElement('div');
      shadowRoot.appendChild(shadowContent);
      return shadowContent;
    }
  }

  function divRootContainer(swaggerContainer) {
    const divRoot = document.createElement('div');
    // When we use the div as the root we have to put the style on the document head.
    const styleLink = document.createElement('link');
    styleLink.rel = "stylesheet";
    styleLink.href = "$services.webjars.url('org.webjars:swagger-ui', 'swagger-ui.css')";
    document.head.appendChild(styleLink);
    swaggerContainer[0].appendChild(divRoot)
    return divRoot
  }


  function initializeShadowDOM() {
    $(".swaggeroot").each(function () {
      let swaggerContainer = $(this);
      let swaggerUIContainer;
      if (XWiki.contextaction === 'export') {
        // In export mode the shadow dom is ignored by paged.js
        swaggerUIContainer = divRootContainer(swaggerContainer)
      } else {
        swaggerUIContainer = shadowRootContainer(swaggerContainer);
      }

      const swaggerParameters = swaggerContainer.data();
      source = determinateSource(swaggerParameters, swaggerContainer);
      delete swaggerParameters.url;
      delete swaggerParameters.token;
      // tagsSorter and operationsSorter are using enum pickers and 'default' is a keyword in java so I wrote it
      // with caps and I have to convert it to lowercase to work with SwaggerUi.
      swaggerParameters.tagsSorter = swaggerParameters.tagsSorter.toLowerCase();
      swaggerParameters.operationsSorter = swaggerParameters.operationsSorter.toLowerCase();

      source[0] === 'spec' ? swaggerParameters.spec = source[1] : swaggerParameters.url = source[1];
      swaggerParameters.domNode = swaggerUIContainer;
      if (swaggerParameters.supportedSubmitMethods != null) {
        swaggerParameters.supportedSubmitMethods.toLowerCase() === 'None' ? [] :
          swaggerParameters.supportedSubmitMethods = Array.from(swaggerParameters.supportedSubmitMethods.split(','));
      }
      SwaggerUIBundle(
        swaggerParameters
      );
    });
  }

  function determinateSource(swaggerParameters, swaggerContainer) {
    let content = swaggerContainer.find('.swaggercontent').attr('data-content');
    if (content.length > 0) {
      if (content === "" || content == null) {
        return ["spec", {}];
      }
      try {
        // First, try to parse as JSON
        return ['spec', JSON.parse(content)];
      } catch (jsonError) {
        try {
          // If JSON parsing fails, try to parse as YAML
          return ['spec', yamltojson.load(content)];
        } catch (yamlError) {
          // If both parsing attempts fail, log the errors and notify the user
          console.error('Error parsing the content as JSON:', jsonError);
          console.error('Error parsing the content as YAML:', yamlError);
          new XWiki.widgets.Notification(l10n['invalid'], 'error');
          // Failsafe to display something in the UI
          return ['spec', {}];
        }
      }
    } else if (swaggerParameters.url != null) {
      return ['url', swaggerParameters.url];
    } else {
      return ['spec', {}];
    }
  }


  // The shadow dom will disappear when entering edit mode and this makes sure that is loaded back and we see the
  // SwaggerUI in edit mode.
  $(document).on('xwiki:dom:updated', function () {
    console.log("xwiki:dom:loaded event triggered. Initializing Shadow DOM.");
    initializeShadowDOM();
  });

  // Trigger initialization immediately in case the event has already fired
  if (document.readyState === 'complete' || document.readyState === 'interactive') {
    initializeShadowDOM();
  }
});
